const offset = 19800000;

let schedule_en = flow.get("schedule").toString();
let days = flow.get("days").toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });

// Alarm 1
let alarm_1 = parseInt(flow.get("alarm_1_time")) + offset;
let alarm_1_en = flow.get("alarm_1_en").toString();
let alarm_1_hour = (Math.floor(alarm_1 / 3600000)).toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });
let alarm_1_min = (Math.floor(alarm_1 / 60000) % 60).toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });

// Alarm 2
let alarm_2 = parseInt(flow.get("alarm_2_time")) + offset;
let alarm_2_en = flow.get("alarm_2_en").toString();
let alarm_2_hour = (Math.floor(alarm_2 / 3600000)).toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });
let alarm_2_min = (Math.floor(alarm_2 / 60000) % 60).toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });

// Alarm 3
let alarm_3 = parseInt(flow.get("alarm_3_time")) + offset;
let alarm_3_en = flow.get("alarm_3_en").toString();
let alarm_3_hour = (Math.floor(alarm_3 / 3600000)).toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });
let alarm_3_min = (Math.floor(alarm_3 / 60000) % 60).toLocaleString('en-US', { minimumIntegerDigits: 2, useGrouping: false });

msg.payload = schedule_en + alarm_1_en + alarm_1_hour + alarm_1_min + alarm_2_en + alarm_2_hour + alarm_2_min + alarm_3_en + alarm_3_hour + alarm_3_min + days;
msg.topic = "190622R/alarms";

return msg;

