let date = new Date();
let hour = date.getHours();
let minute = date.getMinutes();
let schedule = flow.get("schedule");
let days = flow.get("days");

// Alarm 1
let alarm_1_en = flow.get("alarm_1_en");
let alarm_1_date = new Date(flow.get("alarm_1_time"));

// Alarm 2
let alarm_2_en = flow.get("alarm_2_en");
let alarm_2_date = new Date(flow.get("alarm_2_time"));

// Alarm 3
let alarm_3_en = flow.get("alarm_3_en");
let alarm_3_date = new Date(flow.get("alarm_3_time"));

const alarm_hours = [alarm_1_date.getHours(), alarm_2_date.getHours(), alarm_3_date.getHours()];
const alarm_minutes = [alarm_1_date.getMinutes(), alarm_2_date.getMinutes(), alarm_3_date.getMinutes()];
const alarm_en = [alarm_1_en, alarm_2_en, alarm_3_en];

if (schedule && days > 0) {
    for (let i = 0; i < 3; i++) {
        if (alarm_en[i] && alarm_hours[i] == hour && alarm_minutes[i] == minute) {
            msg.payload = i;
            return msg;
        }
    }
}

