let min_temp = parseInt(flow.get("min_temp"));
let max_temp = parseInt(flow.get("max_temp"));
let temp = parseInt(msg.payload)

if (temp < min_temp) {
    msg.payload = "Current Temperature is lower than the Minimum Temperature";
    msg.topic = "Temperature Warning"
    return msg;
} else if (temp > max_temp) {
    msg.payload = "Current Temperature is higher than the Maximum Temperature";
    msg.topic = "Temperature Warning"
    return msg;
}

